const speak = (texts, lang = 'zh-TW') => {
  if (!('speechSynthesis' in window)) {
    console.error('Speech Synthesis not supported in this browser.');
    alert('æŠ±æ­‰ï¼Œæ‚¨çš„ç€è¦½å™¨ä¸æ”¯æŒèªžéŸ³åŠŸèƒ½ã€‚');
    return;
  }
  window.speechSynthesis.cancel();
  const textsToSpeak = (Array.isArray(texts) ? texts : [texts]).filter(text => text);
  if (textsToSpeak.length === 0) {
    return;
  }
  let currentIndex = 0;
  const speakNext = () => {
    if (currentIndex >= textsToSpeak.length) {
      return;
    }
    const text = textsToSpeak[currentIndex];
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = lang;
    utterance.rate = 0.9;
    utterance.pitch = 1.1;
    utterance.volume = 1;
    utterance.onend = () => {
      currentIndex++;
      speakNext();
    };
    utterance.onerror = (event) => {
      console.error('An error occurred during speech synthesis:', event);
      currentIndex++;
      speakNext();
    };
    window.speechSynthesis.speak(utterance);
  };
  speakNext();
};

